extension.property:
wildfly:
    image: ${docker.image.name}
    exposedPorts: [
        8080/tcp,
        9990/tcp
    ]
    env: [
        WILDFLY_MANAGEMENT_USER=${wildfly.mgmt.username},
        WILDFLY_MANAGEMENT_PASSWORD=${wildfly.mgmt.password}
    ]
    portBindings: [
        8080/tcp,
        9990/tcp
    ]
    await:
        strategy: org.wildfly.camel.test.docker.WildFlyAwaitStrategy
